import React, {useState, useRef, useEffect} from "react";
import TodoList from "./TodoList";

const LOCAL_STORAGE_KEY = 'todoApp.todos'
let id = 0;

function App()
{
  const [todos, setTodos] = useState([])  //Create a list "todos" and a function "setTodos" for modifying the list of todos. useState sets the "todos" list to a default empty list "[]"
  const todoNameRef = useRef()            //Creating variable for fetching an inputstring from user


  // //Create effect function that loads the todos list from local storage
  // useEffect(() => 
  // {
  //   const storedTodos = JSON.parse(localStorage.getItem(LOCAL_STORAGE_KEY))        //Fetch local stored todos list
