  // //Creating function that saves the state of the todos list every time the todos list is modified
  // useEffect(() => 
  // {
  //  localStorage.setItem(LOCAL_STORAGE_KEY, JSON.stringify(todos))
  // }, [todos])

  //Function that marks a todo as completed
  function CheckTodo(id)
  {
    const newTodos = [...todos];  //Copy the current todos list


  }

  function AddTodo(event) 
  {
    const name = todoNameRef.current.value //Fetching the value of the user input string
    if(name === "") return;                 //If user inputs an empty string, just return

    id += 1;
    setTodos(prevTodos => {                 //Modifying the todos list
      console.log("ID", id);
      return [...prevTodos, {id:id, name: name, completed: false}]              //Adding new todo to the todos list
    })
    todoNameRef.current.value = null;       //Clear out the text on the input bar, just for estetics
  }

  return(
    <>                                          {/*Can not return two things, so wrap the two things in one element by using <> </> */}
      <TodoList todoList = {todos}/>                               {/*Call TodoList component and pass inn the list of todos*/}
      <input ref={todoNameRef} type="text"/>                      {/*Fetching input from user */}
      <button onClick={AddTodo}>Add Todo</button>                 {/*Creating button for adding things to ToDo */}
      <button>Clear Completed Todos</button>    {/*Creating button for clearing completed todos*/}
      <div>0 things to do</div>                 {/*Displaying how many todos left*/}
    </>
  )
}

export default App;











