import React, {useState, useEffect, useRef} from "react";
import axios from "axios";

let id = 0;

//Main starting function
function Main ()
{
  const [ToDoList, setToDoList] = useState([])   //Create a list "ToDoList" and a function that modifies the "ToDoList". The list is initialized to empty "[]"
  const input = useRef()                   //Creating variable for fetching an input-string from user
  //Function that fetches requests from a URL and places them in a list
  function GetRequests()
  {
    axios.get(`https://jsonplaceholder.typicode.com/todos/`).then(result => {
      const list = result.data;
      console.log(list.length);
      setToDoList(list);
    })
  }

  function AddToDo()
  {
    const inputString = input.current.value;    //Fetching the value of the input string

    if(inputString === "") return;              //Returning if input string is empty

    id += 1;                                    //Incrementing id variable to get unique id

    setToDoList(prevTodos => {
      return [...prevTodos, {id:id, name: inputString, done: false}]  //Adding new To Do to list 
    })
    input.current.value = null;                 //Clear out the input bar to be empty
  }

  function ClearTodos()
  {

  }

  //What is displayed on website
  return(
    <>                                              {/*Function requires that only one element is returned, so wrap other elements in one element*/}
      <h1>Online To Do List</h1>                    {/*Creating header*/}
      <input ref={input} type="text"/>                          {/*Fetching input from user*/}
      <button onClick={AddToDo}>Add To Do</button>                    {/*Creating button for adding ToDos to list*/}
      <button onClick={ClearTodos}>Clear completed To Do's</button>
      {/* <GetRequests/> */}
      <div>{ToDoList.map((item) => (
        <p>{item.name}</p>))}
      </div>
    </> 
  )
}

export default Main;
