import React, {useState, useEffect} from "react";

let id = 0;

//Main starting function
function Main()
{
  const [ToDoList, setTodoList] = useState([{id: 1, string: "Spille", completed: false}])   //Create a list "ToDoList" and a function that modifies the "ToDoList". The list is initialized to empty "[]"

  //Function that fetches requests from a URL and places them in a list
  function GetRequests()
  {
    useEffect(() =>
    {
      fetch(`https://jsonplaceholder.typicode.com/todos/`).then(result => {
        const list = result.formData;
        setTodoList(list);
      })
    })
  }


  // //Function that adds a string and id to the ToDoList
  // function AddTodo()
  // {
  //   const toDostring = input_string.current.value //Fetching the value of the input string 
  //   if(toDoString === "") return;                 //Return if an empty string has been inputed

  //   id += 1;


  // }

  //What is displayed on website
  return(
    <>                                              {/*Function requires that only one element is returned, so wrap other elements in one element*/}
      <h1>Online To Do List</h1>                    {/*Creating header*/}
      <input type="text"/>                          {/*Fetching input from user*/}
      <button>Add To Do</button>                    {/*Creating button for adding ToDos to list*/}
      {/* <div>{TodoList.filter(ToDo => !ToDo.completed).length} things to do</div>                 Displaying how many todos left */}
    </> 
  )
}

export default Main;
