  //What is displayed on website
  return(
    <>                                              {/*Function requires that only one element is returned, so wrap other elements in one element*/}
      <h1>Online To Do List</h1>                    {/*Creating header*/}
      <input type="text"/>                          {/*Fetching input from user*/}
      <button>Add To Do</button>                    {/*Creating button for adding ToDos to list*/}
      <div>{TodoList.filter(ToDo => !ToDo.completed).length} things to do</div>                 Displaying how many todos left
    </> 
  )
}

export default Main;
