import React, {useState, useEffect, useRef} from "react";
import TodoList from "./ToDoList";
import axios from "axios";

let id = 0;

//Main starting function
function Main ()
{
  const [ToDoList, setToDoList] = useState([])   //Create a list "ToDoList" and a function that modifies the "ToDoList". The list is initialized to empty "[]"
  const input = useRef()                   //Creating variable for fetching an input-string from user
  const [Get, setGet] = useState(0);
  const [Post, setPost] = useState(0);
  const [Delete, setDelete] = useState(-1);

  useEffect(() => 
  {

    // axios.get(`https://jsonplaceholder.typicode.com/todos/`).then(result => {
    axios.get(`http://localhost:5000/api/items/`).then(result => {
      const list = result.data;
      console.log(list);
      setToDoList(list);
    });
  }, [Get]);



  // //Function that adds a To Do to the ToDoList
  // function AddToDo()
  // {
  //   const inputString = input.current.value;    //Fetching the value of the input string

  //   if(inputString === "") return;              //Returning if input string is empty

  //   id += 1;                                    //Incrementing id variable to get unique id

  //   setToDoList(prevTodos => {
  //     return [...prevTodos, {id:id, name: inputString, done: false}]  //Adding new To Do to list 
  //   })
  //   input.current.value = null;                 //Clear out the input bar to be empty
  // }

  //Add
  useEffect(() =>
  {
    if(Post === 0) return;

    const inputString = input.current.value;

    axios.post(`http://localhost:5000/api/items/`, {done: false, id: 1, name:inputString}).then(result => {
      setGet(Math.random());
      setPost(0);
    })
  }, [Post]);

  //Delete
  useEffect(() => 
  {
    axios.delete(`http://localhost:5000/api/items/`, )
  })

  //Function that replaces the origianl ToDoList with one that does not contain uncompleted To Do's
  function ClearToDos()
  {
    const newToDoList = ToDoList.filter(item => !item.done);    //Create copy of ToDoList but put only the ToDo's that are not complete 
    setToDoList(newToDoList);
  }

  //Function that marks a todo as completed
  function CheckToDo(id)
  {
    const newToDoList = [...ToDoList];                      //Copy the current toDoList
    const todo = newToDoList.find(item => item.id === id);  //Going through list and fetching the one ToDo that we are going to modify
    todo.done = !todo.done;                       //Set the state of the one todo to the opposite of what it was
    setToDoList(newToDoList);                               //Set the todos lsit to the new todos list
  }

  //What is displayed on website
  return(
    <>                                                                {/*Function requires that only one element is returned, so wrap other elements in one element*/}
      <h1>"Online" To Do List</h1>                                      {/*Creating header*/}
      <input ref={input} type="text"/>                                {/*Fetching input from user*/}
      {/* <button onClick={AddToDo}>Add To Do</button>                    Creating button for adding ToDos to list */}
      <button onClick={() => setPost(1)}>Add To Do</button>                    
      <button onClick={ClearToDos}>Clear completed To Do's</button>   {/*Displaying how many todos left*/}
      <ul>
        {ToDoList.items && ToDoList.items.map(item => {                                                       //Going through every variable in todoList and call each variable "item"
            return(
            <div key={item.id}>
              <input type="checkbox"/>
              <button>Delete</button>
              {item.name}
            </div>
            )
        })}
      </ul>
      {/* <h2>{ToDoList.items && ToDoList.items.filter(item => !item.done).length} things left to do:</h2>      */}
      <TodoList todoList = {ToDoList} checkTodo={CheckToDo}/>
    </> 
  )
}

export default Main;
