import React from "react";
import {v4 as uuidv4} from "uuid";

const initialList = [
  {id: 60, name: "Magnus"},
  {id: 69, name: "Oda"}
]

const App = () => {
  const [list, setList] = React.useState(initialList);
  const [name, input_field] = React.useState("");

  //Track input field's state
  function handleChange(event) {
    input_field(event.target.value);
  }

  //Add an item to list
  function handleAdd() {
    const newList = list.concat({name, id: uuidv4() });

    setList(newList);

    //Set input field back to empty state
    input_field("mafakka");
  }

  return(
    <div>
      <div>
        <input type="text" value={name} onChange={handleChange}/>     {/*Create input field that calls function handleChange*/}
        <button type="button" onClick={handleAdd}>                    {/*Create button that calls handleAdd when pushed*/}
          Add
        </button>
      </div>

      <p>
        {list.map((item) => (
          <li key={item.id}>{item.name}</li>
        ))}
      </p>
    </div>
  );
}

export default App;
