import React, {useState, useEffect} from "react";
import axios from "axios";


function App()
{ 
  //current count,function to update the count 
  const [count, setCount] = useState(4);  //useState sets count variable to 4 from start

  //Declare function that decrements the count
  function decrementCount()
  {
    //Call setCount function that allows us to update the state of "count"
    //BUT we make a new variable that takes the current state of the "count" variable and decrement it
    setCount(fakeCount => fakeCount - 1);
  }

  //Declare function that increments the count
  function incrementCount()
  {
    //Call setCount function that allows us to update the state of "count"
    //BUT we make a new variable that takes the current state of the "count" variable and decrement it
    setCount(fakeCount => fakeCount + 1);
  }

  return(
    <>
        {/*Create button that calls a function when clicked*/}
        <button onClick={decrementCount}>-</button>
        <span>{count}</span>
        <button onClick={incrementCount}>+</button>

      </>
    )
}

export default App;





