import React from "react";

//Function that displays a checkbox next to each To Do and marks a To Do as completed
export default function Todo({eachTodo, checkTodo})
{   
    function handleTodoClick()
    {
        checkTodo(eachTodo.id)  //Call checkTodo function 
    }

    return(
        <div>
            <label>
                <input type="checkbox" checked={eachTodo.complete} onChange={handleTodoClick}/> {/*Creating input checkbox that marks each todo as complete*/}
                {eachTodo.name}
            </label>
        </div>
    )
}
